import React, { useState } from 'react';
import {
  Container,
  Paper,
  Typography,
  TextField,
  Button,
  Grid,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  Box,
  Table,
  TableBody,
  TableCell,
  TableContainer,
  TableHead,
  TableRow,
} from '@mui/material';
import { GRADE_LABELS, calculateSGPA, calculateCGPA } from '../constants/grades';

const CGPACalculator = () => {
  const [semesters, setSemesters] = useState([{ courses: [] }]);
  const [currentSemester, setCurrentSemester] = useState(1);
  const [course, setCourse] = useState({
    name: '',
    credits: '',
    grade: '',
  });

  const handleAddCourse = () => {
    if (!course.name || !course.credits || !course.grade) return;

    const updatedSemesters = [...semesters];
    if (!updatedSemesters[currentSemester - 1]) {
      updatedSemesters[currentSemester - 1] = { courses: [] };
    }
    updatedSemesters[currentSemester - 1].courses.push({ ...course });
    setSemesters(updatedSemesters);
    setCourse({ name: '', credits: '', grade: '' });
  };

  const handleAddSemester = () => {
    setSemesters([...semesters, { courses: [] }]);
    setCurrentSemester(semesters.length + 1);
  };

  const handleRemoveCourse = (semesterIndex, courseIndex) => {
    const updatedSemesters = [...semesters];
    updatedSemesters[semesterIndex].courses.splice(courseIndex, 1);
    setSemesters(updatedSemesters);
  };

  return (
    <Container maxWidth="md" sx={{ py: 4 }}>
      <Paper elevation={3} sx={{ p: 3 }}>
        <Typography variant="h4" gutterBottom align="center">
          CBIT CGPA Calculator
        </Typography>

        <Grid container spacing={3} sx={{ mb: 3 }}>
          <Grid item xs={12} md={4}>
            <TextField
              fullWidth
              label="Course Name"
              value={course.name}
              onChange={(e) => setCourse({ ...course, name: e.target.value })}
            />
          </Grid>
          <Grid item xs={12} md={4}>
            <TextField
              fullWidth
              type="number"
              label="Credits"
              value={course.credits}
              onChange={(e) => setCourse({ ...course, credits: e.target.value })}
            />
          </Grid>
          <Grid item xs={12} md={4}>
            <FormControl fullWidth>
              <InputLabel>Grade</InputLabel>
              <Select
                value={course.grade}
                label="Grade"
                onChange={(e) => setCourse({ ...course, grade: e.target.value })}
              >
                {GRADE_LABELS.map((grade) => (
                  <MenuItem key={grade} value={grade}>
                    {grade}
                  </MenuItem>
                ))}
              </Select>
            </FormControl>
          </Grid>
        </Grid>

        <Box sx={{ mb: 3, display: 'flex', gap: 2 }}>
          <Button variant="contained" onClick={handleAddCourse}>
            Add Course
          </Button>
          <Button variant="outlined" onClick={handleAddSemester}>
            Add Semester
          </Button>
        </Box>

        {semesters.map((semester, semesterIndex) => (
          <Box key={semesterIndex} sx={{ mb: 4 }}>
            <Typography variant="h6" gutterBottom>
              Semester {semesterIndex + 1}
            </Typography>
            <TableContainer component={Paper}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Course Name</TableCell>
                    <TableCell>Credits</TableCell>
                    <TableCell>Grade</TableCell>
                    <TableCell>Action</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {semester.courses.map((course, courseIndex) => (
                    <TableRow key={courseIndex}>
                      <TableCell>{course.name}</TableCell>
                      <TableCell>{course.credits}</TableCell>
                      <TableCell>{course.grade}</TableCell>
                      <TableCell>
                        <Button
                          color="error"
                          onClick={() => handleRemoveCourse(semesterIndex, courseIndex)}
                        >
                          Remove
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            <Typography variant="subtitle1" sx={{ mt: 1 }}>
              SGPA: {calculateSGPA(semester.courses)}
            </Typography>
          </Box>
        ))}

        <Typography variant="h5" sx={{ mt: 4 }}>
          Overall CGPA: {calculateCGPA(semesters)}
        </Typography>
      </Paper>
    </Container>
  );
};

export default CGPACalculator;
